import { Button, Container, TextField, Grid, Snackbar, Alert, AlertTitle } from '@mui/material';
import './App.css';
import {useState} from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import CloseIcon from '@mui/icons-material/Close';
import IconButton from '@mui/icons-material/Close';

const keys = [
    { text: '7', value: 7 },
    { text: '8', value: 8 },
    { text: '9', value: 9 },
    { text: '4', value: 4 },
    { text: '5', value: 5 },
    { text: '6', value: 6 },
    { text: '1', value: 1 },
    { text: '2', value: 2 },
    { text: '3', value: 3 },
    { text: '', value: 'null' },
    { text: '0', value: 0 },
    { text: 'Отправить', value: 'send' },
];

function Input() {
    const [value, setValue] = useState(0);
    const [snackbarOpen, setSnackbarOpen] = useState(0);
  const navigate = useNavigate();

  function handleSend(){
    axios.post('/acc', {
        sum: value
    })
    .then((res) => {
        navigate('/result');
    })
    .catch((error) => {
        handleClick();
        console.log(error);
    })
  }

  const handleClick = () => {
    setSnackbarOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setSnackbarOpen(false);
  };

  const action = (
    <>
      <Alert severity="error">
        < AlertTitle>Ошибка</AlertTitle>
        Ошибка при отправке запросе
      </Alert>
      <IconButton
        size="small"
        aria-label="close"
        color="inherit"
        onClick={handleClose}
      >
        <CloseIcon fontSize="small" />
      </IconButton>
    </>
  );

  return (
    <>
    <Snackbar 
        open={snackbarOpen}
        autoHideDuration={3000}
        onClose={handleClose}
        message="Note archived"
        action={action}
      />
    <Container sx={{display: 'flex', height: 100+'vh', justifyContent: 'center', alignItems: 'center'}}>
        <Grid spacing={2} container sx={{ maxWidth: 500}}>
            <Grid item xs={12} sm={12} md={12}>
                <TextField sx={{height: '100%', width: '100%'}} id="summ" label="Сумма к снятию" variant="filled" />
            </Grid>
            {keys.map(item => (
                <Grid item xs={4} sm={4} md={4}>
                    {(item.value !== 'send') ? (
                        <Button sx={{height: '100%', width: '100%'}} variant="outlined" key={item.value} onClick={() => setValue(+`${value} + ${item.value}`)}>{item.value}</Button>
                    ) : (item.value !== 'null') ? (
                        <Button sx={{height: '100%', width: '100%'}} variant="contained" key={item.value} onClick={() => handleSend()}>{item.value}</Button>
                    ) : (
                        <Button sx={{height: '100%', width: '100%'}} variant="contained" key={item.value} disabled>{item.value}</Button>
                    )}
                </Grid>
                )
            )}
        </Grid>
    </Container>
    </>
  );
}

export default Input;
